class DSU:
    def __init__(self, N):
        self.e = [-1] * N

    def get(self, x):
        if self.e[x] < 0:
            return x
        self.e[x] = self.get(self.e[x])
        return self.e[x]

    def unite(self, x, y):
        x = self.get(x)
        y = self.get(y)
        if x == y:
            return False
        if self.e[x] > self.e[y]:
            x, y = y, x
        self.e[x] += self.e[y]
        self.e[y] = x
        return True

