import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.Arrays;
import java.util.Comparator;
import java.util.PriorityQueue;
import java.util.StringTokenizer;

public class EmailFiling {

    public static void main(String[] args) throws IOException {
        BufferedReader in = new BufferedReader(new InputStreamReader(System.in));
        StringBuilder out = new StringBuilder();
        for (int t = Integer.parseInt(in.readLine()); t > 0; t--) {
            StringTokenizer tokenizer = new StringTokenizer(in.readLine());
            int m = Integer.parseInt(tokenizer.nextToken());
            int n = Integer.parseInt(tokenizer.nextToken());
            int k = Integer.parseInt(tokenizer.nextToken());
            tokenizer = new StringTokenizer(in.readLine());
            int[] folder = new int[n];
            int[] rem = new int[m];
            for (int j = 0; j < n; j++) {
                folder[j] = Integer.parseInt(tokenizer.nextToken()) - 1;
                rem[folder[j]]++;
            }
            int firstFolder = 0;
            int firstEmail = 0;
            int lastEmail = k - 1;
            boolean[] filed = new boolean[n];
            PriorityQueue<Integer> pq = new PriorityQueue<>(Comparator.comparingInt(j -> folder[j]));
            for (int j = 0; j < k; j++) {
                pq.add(j);
            }
            while (lastEmail < n - 1 && firstFolder < m) {
                if (rem[firstFolder] == 0) {
                    firstFolder++;
                } else if (!pq.isEmpty() && folder[pq.peek()] < firstFolder + k) {
                    int j = pq.remove();
                    if (j >= firstEmail) {
                        filed[j] = true;
                        rem[folder[j]]--;
                        lastEmail++;
                        pq.add(lastEmail);
                    }
                } else {
                    while (firstEmail < n && filed[firstEmail]) {
                        firstEmail++;
                    }
                    firstEmail++;
                    lastEmail++;
                    pq.add(lastEmail);
                }
            }
            String answer = "YES";
            while (firstFolder < m) {
                if (rem[firstFolder] == 0) {
                    firstFolder++;
                } else {
                    if (pq.isEmpty()) {
                        answer = "NO";
                        break;
                    }
                    int j = pq.remove();
                    if (j >= firstEmail && !filed[j]) {
                        if (folder[j] >= firstFolder + k) {
                            answer = "NO";
                            break;
                        }
                        filed[j] = true;
                        rem[folder[j]]--;
                        while (firstEmail > 0) {
                            firstEmail--;
                            if (!filed[firstEmail]) {
                                pq.add(firstEmail);
                                break;
                            }
                        }
                    }
                }
            }
            out.append(answer).append('\n');
        }
        System.out.print(out);
    }
}

