    current_shell = starting_shell
    correct = 0
    for i in range(N):
        if A[i] == current_shell:
            current_shell = B[i]
        elif B[i] == current_shell:
            current_shell = A[i]
        if current_shell == G[i]:
            correct += 1
    return correct

