def union_init(d, s):
    for i in range(s):
        d[i] = i


def union_query(d, n):
    res = n
    while d[res] != res:
        res = d[res]
    while d[n] != n:
        m = d[n]
        d[n] = res
        n = m
    return res


def union_merge(d, x, y):
    x = union_query(d, x)
    y = union_query(d, y)
    if x == y:
        return -1
    d[x] = y
    return 1


