def successor_index(v, A, N):
    low, high = 0, N
    while low < high:
        mid = (low + high) // 2
        if A[mid] < v:
            low = mid + 1
        else:
            high = mid
    return low

def num_in_range(a, b, A, N):
    return successor_index(b + 1, A, N) - successor_index(a, A, N)

